func threeSumMulti(arr []int, target int) int {
	ret := 0
	counter := make([]int, 101)
	for _, i2 := range arr {
		counter[i2]++
	}
	fmt.Println(counter)
	for i := 0; i <= 100; i++ {
		for j := i; j <= 100; j++ {
			k := target - i - j
			if k > 100 || k < 0 {
				continue
			}
			if i == j && j == k {
				ret += counter[i] * (counter[i] - 1) * (counter[i] - 2) / 6
			} else if i == j && j != k {
				ret += counter[i] * (counter[i] - 1) * counter[k] / 2
			} else if j < k {
				ret += counter[i] * counter[j] * counter[k]
			}
		}

	}
	return ret % (1000000007)
}

//x choose n combination
func combination(x, n int) int {
	return factorialCal(x) / (factorialCal(x-n) * factorialCal(n))

}
func factorialCal(number int) int {
	if number < 2 {
		return 1
	}
	return (number * factorialCal(number-1)) % 1000000007
}
